import { Component } from '@angular/core';
import { LocalService } from '../local.service';

@Component({
  selector: 'app-sign',
  templateUrl: './sign.component.html',
  styleUrls: ['./sign.component.css']
})
export class SignComponent {

  constructor (private localStore: LocalService) {

  }

  login : string = ''
  password : string = ''

  createNewUser () : void {

  }
}
<div>
    <div style="width: 20%; margin-left: 40%; margin-top: 10px">
        Username <br>
        <input style="width: 100%;" type="text">
    </div>
    <div style="width: 20%; margin-left: 40%; margin-top: 10px;">
        Password <br>
        <input [(ngModel)]="password" placeholder="password" style="width: 100%;" type="password">
    </div>
    <div style="width: 20%; margin-left: 40%; margin-top: 10px">
        <button ng-click="createNewUser()" style="margin-left: 38%;">Sign Up</button>
    </div>
</div>

