import { Component } from '@angular/core';
import { LocalService } from '../local.service';

@Component({
  selector: 'app-sign',
  templateUrl: './sign.component.html',
  styleUrls: ['./sign.component.css']
})

export class SignComponent {

  constructor (private localStore: LocalService) {}

  login : string = '';
  password : string = '';

  createNewUser () : void {
    if (this.localStore.getData(this.login) == null) {
      // can create a new user;
      if (this.login != '' && this.password != '') {
        this.localStore.saveData(this.login, this.password);
        alert('you signed up!');
      }
    } else {
      // user already in store so alert
      alert('login already used');
    }
  }
}
<div>
    <div style="width: 20%; margin-left: 40%; margin-top: 10px">
        Username <br>
        <input [(ngModel)]="login" style="width: 100%;" type="text" required>
    </div>
    <div style="width: 20%; margin-left: 40%; margin-top: 10px;">
        Password <br>
        <input [(ngModel)]="password" placeholder="" style="width: 100%;" type="password" required>
    </div>
    <div style="width: 20%; margin-left: 40%; margin-top: 10px">
        <button (click)="createNewUser()" style="margin-left: 38%;">Sign Up</button>
    </div>
</div>

login: {{this.login}}
password: {{this.password}}
