import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';

import { LocalService } from '../local.service';

enum arrors {up = '↑', down = '↓', def = '○'};
enum options {'Важно', 'Важно но лень', 'Не важно'};

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})

export class HomeComponent {
  constructor (private localStore: LocalService, private route : ActivatedRoute, private router : Router, fb: FormBuilder) {
    this.form = fb.group({
      description: ['', Validators.required],
      priority: ['', Validators.required],
      time: ['', Validators.required]
    });
  }

  form: FormGroup;

  text = {
    welcome: 'Welcome',
    needlogin: 'You need to be logged in!',
    cwith: 'Create with',
    modal: 'modal form',
    reactive: 'reactive form',
    task: 'Task',
    priority: 'Priority',
    time: 'Time',
    edit: 'Editing with',
    delete: 'Deleting',
    del: 'delete'
  };

  t : string = '';
  name : string = '';
  login : string = '';
  token : string = '';
  logged : boolean = false; // then change to false and repair token 
  sorted = [arrors.def, arrors.def, arrors.def];
  createVisible : boolean = false;

  // tasks = [
  //   {description: 'Погулять с собакой', priority: 'Важно но лень', time: '06:00'},
  //   {description: 'Дописать функционал', priority: 'Важно', time: '12:15'},
  //   {description: 'Выпить воды', priority: 'Не важно', time: '21:00'},
  //   {description: 'Выучить как отцентровать div', priority: 'Важно', time: '00:00'}
  // ];

  tasks = [{description: '', priority: '', time: ''}];

  ngOnInit () {
    // taking params from URL query; mosty from log and sign pages via RouterChange
    this.route.queryParamMap.subscribe(params => {
        this.login = params.get('UserLogin') || '';
        this.token = params.get('token') || '';
      }
    );

    if (this.login != '' && this.localStore.getData(this.token) === 'true') {
      this.logged = true;
      this.name = this.localStore.getData(this.login + 'name') || '';
      this.tasks = JSON.parse(this.localStore.getData(this.login + 'data') || '');
      this.t = JSON.stringify(this.tasks);
    }
  }

  ngDoCheck () {}

  ngOnDestroy() {
    this.logged = false;
    if (this.token != '' && this.localStore.getData(this.token) === 'true') {
      this.localStore.saveData(this.token, 'false');
      this.localStore.saveData(this.login + 'data', JSON.stringify(this.tasks));
    }
  }

  sortTasks(item : string) : void {
    switch (item) {
      case 'Task':
        this.sorted[1] = arrors.def; this.sorted[2] = arrors.def;
        function cmp1 (a : {description: string}, b : {description: string}) {return a.description < b.description ? -1 : (a.description > b.description ? 1 : 0)}
        switch (this.sorted[0]) {
          case arrors.def:
            this.tasks.sort(cmp1);
            this.sorted[0] = arrors.down;
            break;
          case arrors.down:
            this.tasks.sort(cmp1).reverse();
            this.sorted[0] = arrors.up;
            break;
          case arrors.up:
            this.sorted[0] = arrors.def;
            this.tasks = JSON.parse(this.localStore.getData(this.login + 'data') || '');
        }
        break;
      case 'Priority':
        this.sorted[0] = arrors.def; this.sorted[2] = arrors.def;
        function cmp2 (a : {priority: string}, b : {priority: string}) {return a.priority < b.priority ? -1 : (a.priority > b.priority ? 1 : 0)}
        switch (this.sorted[1]) {
          case arrors.def:
            this.tasks.sort(cmp2);
            this.sorted[1] = arrors.down;
            break;
          case arrors.down:
            this.tasks.sort(cmp2).reverse();
            this.sorted[1] = arrors.up;
            break;
          case arrors.up:
            this.sorted[1] = arrors.def;
            this.tasks = JSON.parse(this.localStore.getData(this.login + 'data') || '');
        }
        break;
      case 'Time':
        this.sorted[0] = arrors.def; this.sorted[1] = arrors.def;
        function cmp3 (a : {time: string}, b : {time: string}) {return a.time < b.time ? -1 : (a.time > b.time ? 1 : 0)}
        switch (this.sorted[2]) {
          case arrors.def:
            this.tasks.sort(cmp3);
            this.sorted[2] = arrors.down;
            break;
          case arrors.down:
            this.tasks.sort(cmp3).reverse();
            this.sorted[2] = arrors.up;
            break;
          case arrors.up:
            this.sorted[2] = arrors.def;
            this.tasks = JSON.parse(this.localStore.getData(this.login + 'data') || '');
        }
    }
  }

  createModal() : void {

  }

  createReactive() : void {
    this.tasks.push({ description: this.form.value.description, 
                      priority: this.form.value.priority, 
                      time: this.form.value.time});
    this.localStore.saveData(this.login + 'data', JSON.stringify(this.tasks));
    this.form.reset();
    this.createVisible = false;
  }

  changeModal(item : {}, i : number) : void {

  } 

  changeReactive(item : {}, i : number) : void {

  }

  deleteItem(i : number) : void {
    if (confirm('You wont to delete?')) {
      this.tasks.splice(i, 1); 
    }
  }
}
<div *ngIf="!this.logged" class="font">
    {{text.needlogin}}
</div>

<div *ngIf="this.logged">
    Name: {{this.name}}
    Login: {{this.login}}
    Password: {{this.login}}
    Tasks: {{this.t}}
    <div *ngIf="this.name != ''">
        {{text.welcome}} {{this.name}}
    </div>
    <div class="pad font">
        {{text.cwith}}
        <button>{{text.modal}}</button> 
        <button (click)="this.createVisible=true">{{text.reactive}}</button>
    </div>
    <div *ngIf="this.createVisible" class="pad font">
        <form [formGroup]="form" (submit)="createReactive()">
            {{this.text.task}} <input formControlName="description"/> <br>
            <input formControlName="priority"/>
            <input formControlName="time"/>
            <button type="submit">Submit</button>
        </form>      
    </div>

    <div class="pad">
        <table class="font border">
            <tr>
                <th class="border">
                    {{text.task}}    
                    <button (click)="sortTasks('Task')">{{sorted[0]}}</button>
                </th>
                <th class="border">
                    {{text.priority}}    
                    <button (click)="sortTasks('Priority')">{{sorted[1]}}</button>
                </th>
                <th class="border">
                    {{text.time}}    
                    <button (click)="sortTasks('Time')">{{sorted[2]}}</button>
                </th>
                <th class="border">{{text.edit}}</th>
                <th class="border">{{text.delete}}</th>
            </tr>
            <tr *ngFor="let item of tasks; index as i;">
                <td class="border">{{item.description}}</td>
                <td class="border">{{item.priority}}</td>
                <td class="border">{{item.time}}</td>
                <td class="border">
                    <button (click)="changeModal(item, i)">{{text.modal}}</button>
                    <button (click)="changeReactive(item, i)">{{text.reactive}}</button>
                </td>
                <td class="border center">
                    <button class="width" (click)="deleteItem(i)">{{text.del}}</button>
                </td>
            </tr>
        </table>
    </div>
</div>
